sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var month = getMonthNumber(g_form.getDisplayBox('month').value);
	function getMonthNumber(month_name){
	switch(month_name){
		case "January" : return 1; 
		case "February" : return 2; 
		case "March" : return 3; 
		case "April" : return 4; 
		case "May" : return 5; 
		case "June" : return 6;
		case "July" : return 7; 
		case "Agust" : return 8; 
		case "September" : return 9; 
		case "October" : return 10; 
		case "November" : return 11; 
		case "December" : return 12; 	
	}
}
	//g_form.addInfoMessage(month);
 	var strt = g_form.getValue('start_date');
 	var strtMon = strt.split("-")[1];
 	var strtYear = strt.split("-")[0];
	var d = new Date();
	var n = d.getFullYear();

	if(strtYear==n&&strtMon==month){
		g_form.clearMessages();
		return;		
	}
	else{
		g_form.clearMessages();
		g_form.addErrorMessage("please select the start date within the current month");
		g_form.clearValue('start_date');
		return false;
	}   
}
