sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	//var mon = g_form.getReference('month', getMonthNumber);

	// You can use below one also

var month = getMonthNumber(g_form.getDisplayBox('month').value);
	function getMonthNumber(month_name){
	switch(month_name){
		case "January" : return 0; 
		case "February" : return 1; 
		case "March" : return 2; 
		case "April" : return 3; 
		case "May" : return 4; 
		case "June" : return 5;
		case "July" : return 6; 
		case "Agust" : return 7; 
		case "September" : return 8; 
		case "October" : return 9; 
		case "November" : return 10; 
		case "December" : return 11; 	
	}
}
	var d = new Date();
	var n = d.getMonth();
	if(month != n){
		g_form.clearMessages();
	g_form.addErrorMessage("Please select the present month");
		g_form.clearValue('month');
		return;
	}
	else{
		g_form.clearMessages();

	}



}
